mapper.select:
 		{CALL newChattingRoom(
 			#{p_id  , mode=IN , jdbcType=VARCHAR}
		    , #{email  , mode=IN , jdbcType=VARCHAR}
		    , #{c_id , mode=OUT, jdbcType=VARCHAR}
 		)}

SELECT *
FROM chatMessage c, member m
WHERE c.sender = m.email AND idx=#{idx}

mapper.update:
 		UPDATE chatMessage
		SET read=0
		WHERE c_id=#{c_id}
			AND sender<>#{sender}
			AND send_date<=#{send_date}

 		UPDATE chatMessage
		SET read=0
		WHERE idx=#{idx}
