mapper.select:
select count(*) from
member
where nickname =#{nickname}

select count(*) from
member
where email =#{email}

mapper.update:
UPDATE member 
SET name = #{name},birth=#{birth},address=#{address},
	phone=#{phone},nickname=#{nickname}
WHERE email=#{email}

UPDATE member 
SET email=#{email}
WHERE birth =#{birth}

UPDATE member 
SET password=#{password}
WHERE email=#{email}
