mapper.select:
select count(*) from
member
where nickname =#{nickname}

select count(*) from
member
where email =#{email}

mapper.update:
UPDATE member 
SET email=#{email},name = #{name},birth=#{birth},address=#{address},
	phone=#{phone},nickname=#{nickname}
WHERE email=#{email}

UPDATE member 
SET email=#{email}
WHERE birth =#{birth}

UPDATE member 
SET password=#{password}
WHERE email=#{email}

update productDetail 
set views = views + TO_NUMBER(1)
where p_id = #{p_id}

		 update productDetail set p_name=#{p_name}
		 , description=#{description}
		 , category=#{category}
		 , price=#{price}
		 , trade=#{trade}
		 , lat=#{lat}
		 , lng=#{lng}
     	 where p_id = #{p_id}
