mapper.update:
update productDetail 
set views = views + TO_NUMBER(1)
where p_id = #{p_id}

		 update productDetail set p_name=#{p_name}
			 , description=#{description}
			 , category=#{category}
			 , price=#{price}
			 , trade=#{trade}
			 , lat=#{lat}
			 , lng=#{lng}
     	 where p_id = #{p_id}
